// Valida checklist e identifica documentos faltantes
const template = $input.first().json;
const documentos = $input.all().slice(1).map(item => item.json);
const casoId = $('Webhook Trigger').item.json.body.caso_id;

const tiposRecebidos = documentos.map(d => d.tipo_documento?.toLowerCase() || '');

const checklistItems = [];
const documentosFaltantes = [];

// Processa template (assumindo que vem como array)
const templateArray = Array.isArray(template) ? template : [template];

for (const item of templateArray) {
  const docNome = item.documento_obrigatorio?.toLowerCase() || '';
  const isObrigatorio = !item.documento_recomendado;

  // Verifica se documento foi recebido
  const recebido = tiposRecebidos.some(tipo => 
    docNome.includes(tipo) || tipo.includes(docNome)
  );

  const checklistItem = {
    checklist_juridico_id: item.id,
    caso_id: casoId,
    status: recebido ? 'recebido' : 'pendente',
    documento_id: recebido ? documentos.find(d => 
      d.tipo_documento?.toLowerCase().includes(docNome) ||
      docNome.includes(d.tipo_documento?.toLowerCase())
    )?.id : null
  };

  checklistItems.push(checklistItem);

  if (!recebido && isObrigatorio) {
    documentosFaltantes.push(item.documento_obrigatorio);
  }
}

// Calcula percentual de completude
const totalObrigatorios = templateArray.filter(i => !i.documento_recomendado).length;
const recebidosObrigatorios = checklistItems.filter(i => i.status === 'recebido' && 
  templateArray.find(t => t.id === i.checklist_juridico_id && !t.documento_recomendado)
).length;

const percentualCompletude = totalObrigatorios > 0 
  ? (recebidosObrigatorios / totalObrigatorios * 100).toFixed(2)
  : 0;

return {
  json: {
    caso_id: casoId,
    checklist_items: checklistItems,
    documentos_faltantes: documentosFaltantes,
    percentual_completude: parseFloat(percentualCompletude),
    total_obrigatorios: totalObrigatorios,
    recebidos_obrigatorios: recebidosObrigatorios
  }
};

