// Organiza documentos e gera linha do tempo
const docs = $input.all().map(item => item.json);

const notasFiscais = docs.filter(d => {
  const tipo = d.tipo_documento?.toLowerCase() || '';
  return tipo.includes('nota') || tipo.includes('nf');
});

const boletos = docs.filter(d => {
  const tipo = d.tipo_documento?.toLowerCase() || '';
  return tipo.includes('boleto');
});

const comprovantes = docs.filter(d => {
  const tipo = d.tipo_documento?.toLowerCase() || '';
  return tipo.includes('comprovante');
});

// Ordena cronologicamente
const docsOrdenados = docs.sort((a, b) => {
  const dateA = new Date(a.data_documento || a.data_upload);
  const dateB = new Date(b.data_documento || b.data_upload);
  return dateA - dateB;
});

return {
  json: {
    caso_id: $('Webhook Trigger').item.json.body.caso_id,
    organizacao: {
      notas_fiscais: notasFiscais.map(d => ({id: d.id, tipo: d.tipo_documento, data: d.data_documento})),
      boletos: boletos.map(d => ({id: d.id, tipo: d.tipo_documento, data: d.data_documento})),
      comprovantes: comprovantes.map(d => ({id: d.id, tipo: d.tipo_documento, data: d.data_documento}))
    },
    linha_tempo: docsOrdenados.map(d => ({id: d.id, tipo: d.tipo_documento, data: d.data_documento}))
  }
};
