// Identifica repetição de indébito
const docs = $input.all().map(item => item.json);
const textos = docs.map(d => d.texto_extraido || '').join(' ').toLowerCase();

const checklist = ['faturas', 'comprovantes de pagamento', 'contrato', 'histórico de comunicação'];
const presentes = docs.map(d => d.tipo_documento?.toLowerCase() || '');
const faltantes = checklist.filter(c => !presentes.some(p => p.includes(c) || c.includes(p)));

// Verifica repetição de indébito
const repeticaoIndebito = textos.includes('repetição') || textos.includes('repeticao') || textos.includes('indébito repetido');

return {
  json: {
    caso_id: $('Webhook Trigger').item.json.body.caso_id,
    documentos_faltantes: faltantes,
    repeticao_indebito: repeticaoIndebito,
    alerta: repeticaoIndebito ? 'Possível repetição de indébito identificada' : null
  }
};
