// Organiza documentos juridicamente
const docs = $input.all().map(item => item.json);

const contratos = docs.filter(d => {
  const tipo = d.tipo_documento?.toLowerCase() || '';
  return tipo.includes('contrato');
});

const aditivos = docs.filter(d => {
  const tipo = d.tipo_documento?.toLowerCase() || '';
  return tipo.includes('aditivo');
});

const provasDescumprimento = docs.filter(d => {
  const tipo = d.tipo_documento?.toLowerCase() || '';
  return tipo.includes('descumprimento') || tipo.includes('violação') || tipo.includes('violacao');
});

const comunicacoes = docs.filter(d => {
  const tipo = d.tipo_documento?.toLowerCase() || '';
  return tipo.includes('email') || tipo.includes('comunicação') || tipo.includes('comunicacao');
});

return {
  json: {
    caso_id: $('Webhook Trigger').item.json.body.caso_id,
    organizacao_juridica: {
      contratos: contratos.map(d => ({id: d.id, tipo: d.tipo_documento})),
      aditivos: aditivos.map(d => ({id: d.id, tipo: d.tipo_documento})),
      provas_descumprimento: provasDescumprimento.map(d => ({id: d.id, tipo: d.tipo_documento})),
      comunicacoes: comunicacoes.map(d => ({id: d.id, tipo: d.tipo_documento}))
    }
  }
};
