// Determina se é consensual ou litigioso e gera checklist dinâmico
const descricao = $input.item.json.body.descricao || '';
const descricaoLower = descricao.toLowerCase();

const isConsensual = descricaoLower.includes('consensual') || descricaoLower.includes('amigável') || descricaoLower.includes('amigavel');

const checklistConsensual = ['Certidão de casamento', 'CPF de ambos', 'RG de ambos', 'Comprovante de residência'];
const checklistLitigioso = [...checklistConsensual, 'Comprovantes de renda', 'Documentos de bens', 'Documentos de dívidas'];

return {
  json: {
    caso_id: $input.item.json.body.caso_id,
    tipo_divorcio: isConsensual ? 'consensual' : 'litigioso',
    checklist: isConsensual ? checklistConsensual : checklistLitigioso,
    separacao_automatica: true
  }
};

// Separa bens, dívidas e provas
const docs = $input.all().slice(1).map(item => item.json);
const tipoDivorcio = $('Determinar Tipo').item.json.tipo_divorcio;

const bens = docs.filter(d => {
  const tipo = d.tipo_documento?.toLowerCase() || '';
  return tipo.includes('imóvel') || tipo.includes('veículo') || tipo.includes('propriedade');
});

const dividas = docs.filter(d => {
  const tipo = d.tipo_documento?.toLowerCase() || '';
  return tipo.includes('dívida') || tipo.includes('divida') || tipo.includes('financiamento');
});

const provas = docs.filter(d => d.classificacao_prova && d.classificacao_prova !== 'outro');

return {
  json: {
    caso_id: $('Determinar Tipo').item.json.caso_id,
    tipo_divorcio: tipoDivorcio,
    bens: bens.map(d => ({id: d.id, tipo: d.tipo_documento})),
    dividas: dividas.map(d => ({id: d.id, tipo: d.tipo_documento})),
    provas: provas.map(d => ({id: d.id, tipo: d.tipo_documento, classificacao: d.classificacao_prova}))
  }
};
