parameters.jsCode:
// Valida documentos específicos de gratuidade
const checklist = $input.all().map(item => item.json);
const casoId = $('Webhook Trigger').item.json.body.caso_id;

const documentosObrigatorios = [
  'IRPF',
  'Contracheque',
  'Extrato bancário',
  'Carteira de trabalho'
];

const documentosFaltantes = [];
const documentosPresentes = [];

for (const doc of documentosObrigatorios) {
  const encontrado = checklist.find(c => 
    c.status === 'recebido' && 
    (c.documento_obrigatorio?.toLowerCase().includes(doc.toLowerCase()) ||
     doc.toLowerCase().includes(c.documento_obrigatorio?.toLowerCase()))
  );

  if (encontrado) {
    documentosPresentes.push(doc);
  } else {
    documentosFaltantes.push(doc);
  }
}

const percentualCompletude = (documentosPresentes.length / documentosObrigatorios.length * 100).toFixed(2);

return {
  json: {
    caso_id: casoId,
    documentos_obrigatorios: documentosObrigatorios,
    documentos_presentes: documentosPresentes,
    documentos_faltantes: documentosFaltantes,
    percentual_completude: parseFloat(percentualCompletude),
    is_completo: documentosFaltantes.length === 0
  }
};

