// Classifica provas por relevância
const docs = $input.all().map(item => item.json);

const provasVinculo = docs.filter(d => {
  const tipo = d.tipo_documento?.toLowerCase() || '';
  return tipo.includes('certidão') || tipo.includes('certidao') || tipo.includes('nascimento');
});

const provasEscolares = docs.filter(d => {
  const tipo = d.tipo_documento?.toLowerCase() || '';
  return tipo.includes('escolar') || tipo.includes('histórico') || tipo.includes('historico');
});

const provasMedicas = docs.filter(d => {
  const tipo = d.tipo_documento?.toLowerCase() || '';
  return tipo.includes('médico') || tipo.includes('medico') || tipo.includes('relatório') || tipo.includes('relatorio');
});

// Ordena por relevância
const todasProvas = [...provasVinculo, ...provasEscolares, ...provasMedicas].sort((a, b) => (b.relevancia || 0) - (a.relevancia || 0));

return {
  json: {
    caso_id: $('Webhook Trigger').item.json.body.caso_id,
    provas_vinculo: provasVinculo.map(d => ({id: d.id, tipo: d.tipo_documento, relevancia: d.relevancia})),
    provas_escolares: provasEscolares.map(d => ({id: d.id, tipo: d.tipo_documento, relevancia: d.relevancia})),
    provas_medicas: provasMedicas.map(d => ({id: d.id, tipo: d.tipo_documento, relevancia: d.relevancia})),
    provas_ordenadas: todasProvas.map(d => ({id: d.id, tipo: d.tipo_documento, relevancia: d.relevancia}))
  }
};
