// Gera quadro comparativo automático
const docs = $input.all().map(item => item.json);

const cartoesPonto = docs.filter(d => {
  const tipo = d.tipo_documento?.toLowerCase() || '';
  return tipo.includes('ponto') || tipo.includes('cartão');
});

const contracheques = docs.filter(d => {
  const tipo = d.tipo_documento?.toLowerCase() || '';
  return tipo.includes('holerite') || tipo.includes('contracheque');
});

const escalas = docs.filter(d => {
  const tipo = d.tipo_documento?.toLowerCase() || '';
  return tipo.includes('escala');
});

return {
  json: {
    caso_id: $('Webhook Trigger').item.json.body.caso_id,
    quadro_comparativo: {
      cartoes_ponto: cartoesPonto.length,
      contracheques: contracheques.length,
      escalas: escalas.length,
      total_documentos: docs.length
    },
    documentos: {
      cartoes_ponto: cartoesPonto.map(d => ({id: d.id, tipo: d.tipo_documento})),
      contracheques: contracheques.map(d => ({id: d.id, tipo: d.tipo_documento})),
      escalas: escalas.map(d => ({id: d.id, tipo: d.tipo_documento}))
    }
  }
};
