// Gera resumo financeiro automático
const docs = $input.all().map(item => item.json);
const valores = docs.flatMap(d => d.valores_encontrados || []);
const total = valores.reduce((sum, v) => sum + v, 0);

const checklist = ['certidão de nascimento', 'comprovantes de renda', 'despesas do menor'];
const presentes = docs.map(d => d.tipo_documento?.toLowerCase() || '');
const faltantes = checklist.filter(c => !presentes.some(p => p.includes(c) || c.includes(p)));

return {
  json: {
    caso_id: $('Webhook Trigger').item.json.body.caso_id,
    resumo_financeiro: {
      total_valores: total,
      valores_identificados: valores,
      quantidade_documentos: docs.length
    },
    documentos_faltantes: faltantes
  }
};
