// Valida checklist e gera linha do tempo
const docs = $input.all().map(item => item.json);
const checklist = ['contrato', 'holerites', 'extrato fgts', 'provas da falta grave'];
const presentes = docs.map(d => d.tipo_documento?.toLowerCase() || '');
const faltantes = checklist.filter(c => !presentes.some(p => p.includes(c) || c.includes(p)));

// Ordena cronologicamente
const docsOrdenados = docs.sort((a, b) => {
  const dateA = new Date(a.data_documento || a.data_upload);
  const dateB = new Date(b.data_documento || b.data_upload);
  return dateA - dateB;
});

return {
  json: {
    caso_id: $('Webhook Trigger').item.json.body.caso_id,
    documentos_faltantes: faltantes,
    linha_tempo: docsOrdenados.map(d => ({id: d.id, tipo: d.tipo_documento, data: d.data_documento})),
    checklist_completo: faltantes.length === 0
  }
};
