// Análise inteligente da descrição do caso
const descricao = $input.item.json.descricao || '';
const tipoAcao = $input.item.json.tipo_acao || '';

// Identifica tipo de ação mais específico
let tipoDetalhado = tipoAcao;
const descricaoLower = descricao.toLowerCase();

if (descricaoLower.includes('companhia aérea') || descricaoLower.includes('aérea')) {
  tipoDetalhado = 'Ação contra Companhia Aérea';
} else if (descricaoLower.includes('negativação') || descricaoLower.includes('spc') || descricaoLower.includes('serasa')) {
  tipoDetalhado = 'Negativação Indevida';
} else if (descricaoLower.includes('consensual')) {
  tipoDetalhado = 'Divórcio Consensual';
} else if (descricaoLower.includes('litigioso')) {
  tipoDetalhado = 'Divórcio Litigioso';
}

// Identifica juízo competente (se aplicável)
let juizoCompetente = null;

if (descricaoLower.includes('consumidor') || descricaoLower.includes('consumo')) {
  // Para relação de consumo, identifica domicílio
  const cepMatch = descricao.match(/\d{5}-?\d{3}/);
  if (cepMatch) {
    juizoCompetente = `Foro do domicílio do consumidor (CEP: ${cepMatch[0]})`;
  } else {
    juizoCompetente = 'Foro do domicílio do consumidor';
  }
}

return {
  json: {
    caso_id: $input.item.json.id,
    tipo_acao: tipoDetalhado,
    juizo_competente: juizoCompetente,
    descricao_analisada: descricao
  }
};
